import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegistrationPage from './pages/RegistrationPage';
import NotFoundPage from './pages/NotFoundPage';
// ...

import PrivateRoute from './components/PrivateRoute';

function App() {
  const [authenticated, setAuthenticated] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = async (email, password) => {
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error(`Error checking authentication: ${response.statusText}`);
      }

      const data = await response.json();

      if (data.success) {
        setAuthenticated(true);
        setCurrentUser(data.user);
      } else {
        console.error('Error checking authentication:', data.message);
      }
    } catch (error) {
      console.error('Error checking authentication:', error.message);
    }
  };

  const handleLogout = () => {
    setAuthenticated(false);
    setCurrentUser(null);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={<LoginPage onLogin={handleLogin} authenticated={authenticated} />}
        />
        <Route path="/register" element={<RegistrationPage />} />
        <PrivateRoute
          path="/"
          element={<HomePage authenticated={authenticated} currentUser={currentUser} onLogout={handleLogout} />}
        />
        <Route path="*" element={<NotFoundPage />} />
      </Routes>
    </Router>
  );
}

export default App;
