// Ваш новый CreateNote

import React, { useState, useContext } from "react";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../components/AuthContext";

const NoteForm = ({ title, body, onTitleChange, onBodyChange, onSubmit, errors }) => (
  <div className="createNote-container">
    <h1 style={{ textAlign: "center" }}>Create new note</h1>
    <input
      type="text"
      className="title-input"
      placeholder="Name"
      value={title}
      onChange={onTitleChange}
    />
    <div className="error">{errors.title && <p>{errors.title}</p>}</div>
    <textarea
      className="note-text"
      placeholder=" Note text..."
      value={body}
      onChange={onBodyChange}
    />
    <div className="button-container">
      <button className="create-button" onClick={onSubmit}>
        Create
      </button>
      <button className="back-button" onClick={() => navigate("/notes")}>
        Back
      </button>
    </div>
  </div>
);

export default function CreateNote() {
  const { user } = useContext(AuthContext);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleBodyChange = (event) => {
    setBody(event.target.value);
  };

  const validateInputs = () => {
    const newErrors = {};

    if (title.trim() === "") {
      newErrors.title = "Title is required";
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (validateInputs()) {
      const newNote = {
        authorId: user.id,
        title,
        text: body,
        createdAt: new Date().toISOString(),
      };

      try {
        const response = await fetch("http://localhost:5001/notes", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newNote),
        });

        if (response.ok) {
          navigate("/notes");
        } else {
          console.error("Failed to create note");
        }
      } catch (error) {
        console.error("Error creating note:", error);
      }
    }
  };

  return (
    <NoteForm
      title={title}
      body={body}
      onTitleChange={handleTitleChange}
      onBodyChange={handleBodyChange}
      onSubmit={handleSubmit}
      errors={errors}
    />
  );
}
