import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { connect } from 'react-redux';
import { useDispatch } from 'react-redux';
import { setUser } from '../redux/actions'; 

function NoteCreator({ user }) {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    document.title = "Create a New Note";
  }, []);

  const handleChangeTitle = (event) => {
    setTitle(event.target.value);
  };

  const handleChangeBody = (event) => {
    setBody(event.target.value);
  };

  const validateInputs = () => {
    const newErrors = {};

    if (title.trim() === "") {
      newErrors.title = "Title is required";
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const createNote = () => {
    if (validateInputs()) {
      const newNote = {
        authorId: user.id,
        title,
        text: body,
        createdAt: new Date().toISOString(),
      };

      fetch("http://localhost:5001/notes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newNote),
      })
        .then((response) => {
          if (response.ok) {
            // Добавляем действие в Redux после успешного создания заметки
            dispatch(setUser(user)); // Подставьте свой экшен
            navigate("/notes");
          } else {
            console.error("Failed to create note");
          }
        })
        .catch((error) => {
          console.error("Error creating note:", error);
        });
    }
  };

  const goBack = () => {
    navigate("/notes");
  };

  return (
    <div>
      <nav className="navigation">
        <div className="navigation-left">Привет, {user.email}</div>
        <div className="navigation-right">
          <Link to="/home" className="navigation-link">
            Обо мне
          </Link>
          <Link to="/notes" className="navigation-link">
            Заметки
          </Link>
          <Link to="/login" className="navigation-link">
            Выйти
          </Link>
        </div>
      </nav>
      <div className="note-container">
        <h1 style={{ textAlign: "center" }}>Создать новую заметку</h1>
        <input
          type="text"
          className="title-input"
          placeholder="Title"
          value={title}
          onChange={handleChangeTitle}
        />
        <div className="error">{errors.title && <p>{errors.title}</p>}</div>
        <textarea
          className="note-text"
          placeholder="Note text..."
          value={body}
          onChange={handleChangeBody}
        />
        <div className="button-group">
          <button className="create-button" onClick={createNote}>
            Создать
          </button>
          <button className="back-button" onClick={goBack}>
            Назад
          </button>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(NoteCreator);
