import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { connect } from 'react-redux';

function Notes({ user }) {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/notes?authorId=${user.id}`
        );
        if (response.ok) {
          const data = await response.json();
          console.log(data);
          if (typeof data === "object") {
            const notesArray = Object.values(data);
            setNotes(notesArray);
          } else {
            console.error("Invalid notes data. Expected an object.", data);
          }
        } else {
          console.error("Failed to fetch notes");
        }
      } catch (error) {
        console.error("Error fetching notes:", error);
      }
    };

    if (user) {
      fetchNotes();
    }
  }, [user]);

  const handleCreateNote = () => {
    navigate("/create-note");
  };

  const handleEditNote = (noteId) => {
    navigate(`/edit-note/${noteId}`);
  };

  const handleDeleteNote = async (noteId) => {
    try {
      const response = await fetch(`http://localhost:5001/notes/${noteId}`, {
        method: "DELETE",
      });
      if (response.ok) {
        setNotes((prevNotes) => prevNotes.filter((note) => note.id !== noteId));
      } else {
        console.error("Failed to delete note");
      }
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, "0");
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const year = date.getFullYear().toString().slice(-2);
    return `${day}/${month}/${year}`;
  };

  return (
    <div>
      <nav>
        <div>Привет, {user.email}</div>
        <div>
          <Link to="/home">Обо мне</Link>
          <Link to="/notes">Заметки</Link>
          <Link to="/login">Выйти</Link>
        </div>
      </nav>
      <div>
        <h1>Заметки</h1>
        <div>
          <button onClick={handleCreateNote}>Добавить новую заметку</button>
        </div>
        <div>
          {notes.length > 0 ? (
            notes.map((note) => (
              <div key={note.id}>
                <Link to={`/view-note/${note.id}`}>
                  <div>
                    <div>
                      <div>{note.title}</div>
                      <div>{formatDate(note.createdAt)}</div>
                    </div>
                  </div>
                </Link>
                <div>
                  <button onClick={() => handleEditNote(note.id)}>✍️</button>
                  <button onClick={() => handleDeleteNote(note.id)}>🗑️</button>
                </div>
              </div>
            ))
          ) : (
            <div>Заметок не найдено.</div>
          )}
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(Notes);
