// UpdateNote.js
import React, { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { connect, useDispatch } from 'react-redux';
import { setUser, editNote, setError, clearError, setLoading } from '../redux/actions';

function UpdateNote({ user, notes, error, isLoading }) {
  const { noteId } = useParams();
  const [note, setNote] = useState(null);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchNoteData = async () => {
      try {
        dispatch(setLoading(true));
        const response = await fetch(`http://localhost:5001/notes/${noteId}`);
        dispatch(setLoading(false));
        if (response.ok) {
          const fetchedNote = await response.json();
          setNote(fetchedNote);
          setTitle(fetchedNote.title);
          setBody(fetchedNote.text);
        } else {
          dispatch(setError("Failed to fetch note data"));
        }
      } catch (error) {
        dispatch(setLoading(false));
        dispatch(setError(`Error fetching note data: ${error}`));
      }
    };

    fetchNoteData();
  }, [noteId, dispatch]);

  const handleChangeTitle = (event) => {
    setTitle(event.target.value);
  };

  const handleChangeBody = (event) => {
    setBody(event.target.value);
  };

  const validateInputs = () => {
    const newErrors = {};

    if (title.trim() === "") {
      newErrors.title = "Title is required";
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const updateNote = () => {
    if (validateInputs()) {
      const updatedNote = {
        id: note.id,
        authorId: note.authorId,
        title,
        text: body,
        createdAt: note.createdAt,
      };

      dispatch(setLoading(true));
      fetch(`http://localhost:5001/notes/${note.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedNote),
      })
        .then((response) => {
          dispatch(setLoading(false));
          if (response.ok) {
            dispatch(editNote(note.id, updatedNote));
            dispatch(setUser(user));
            navigate("/notes");
          } else {
            dispatch(setError("Failed to update note"));
          }
        })
        .catch((error) => {
          dispatch(setLoading(false));
          dispatch(setError(`Error updating note: ${error}`));
        });
    }
  };

  const goBack = () => {
    navigate("/notes");
  };

  return (
    <div>
      <nav className="navigation">
        <div className="navigation-left">Привет, {user.email}</div>
        <div className="navigation-right">
          <Link to="/home" className="navigation-link">
            Обо мне
          </Link>
          <Link to="/notes" className="navigation-link">
            Заметки
          </Link>
          <Link to="/login" className="navigation-link">
            Выйти
          </Link>
        </div>
      </nav>
      <div className="note-container">
        <h1 style={{ textAlign: "center" }}>Редактировать заметку</h1>
        <input
          type="text"
          className="title-input"
          placeholder="Title"
          value={title}
          onChange={handleChangeTitle}
        />
        <div className="error">{errors.title && <p>{errors.title}</p>}</div>
        <textarea
          className="note-text"
          placeholder="Note text..."
          value={body}
          onChange={handleChangeBody}
        />
        <div className="button-group">
          <button className="create-button" onClick={updateNote}>
            Сохранить
          </button>
          <button className="back-button" onClick={goBack}>
            Назад
          </button>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user,
  notes: state.notes,
  error: state.error,
  isLoading: state.isLoading,
});

export default connect(mapStateToProps)(UpdateNote);
