// Notes.js
import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { connect } from 'react-redux';
import { deleteNote, setUser } from '../redux/actions';

function Notes({ user }) {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  const handleDeleteNote = async (noteId) => {
    try {
      const response = await fetch(`http://localhost:5001/notes/${noteId}`, {
        method: "DELETE",
      });
      if (response.ok) {
        dispatch(deleteNote(noteId));  // Dispatch the deleteNote action
        setNotes((prevNotes) => prevNotes.filter((note) => note.id !== noteId));
      } else {
        console.error("Failed to delete note");
      }
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, "0");
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const year = date.getFullYear().toString().slice(-2);
    return `${day}/${month}/${year}`;
  };

  return (
    <div>
      <nav className="navigation">
        <div className="navigation-left">Привет, {user.email}</div>
        <div className="navigation-right">
          <Link to="/home" className="navigation-link">
            Обо мне
          </Link>
          <Link to="/notes" className="navigation-link notes">
            Заметки
          </Link>
          <Link to="/login" className="navigation-link">
            Выйти
          </Link>
        </div>
      </nav>
      <div>
        <h1>Заметки</h1>
        <div className="add-note-button">
          <button onClick={handleCreateNote}>Добавить новую заметку</button>
        </div>
        <div className="notes-container">
          {notes.length > 0 ? (
            notes.map((note) => (
              <div className="note" key={note.id}>
                <Link
                  to={`/view-note/${note.id}`}
                  className="note-content-link"
                >
                  <div className="note-title-container">
                    <div className="note-title">{note.title}</div>
                    <div className="note-date">
                      {formatDate(note.createdAt)}
                    </div>
                  </div>
                </Link>
                <div className="note-actions">
                  <button
                    className="icon-button"
                    onClick={() => handleEditNote(note.id)}
                  >
                    ✍️
                  </button>
                  <button
                    className="icon-button"
                    onClick={() => handleDeleteNote(note.id)}
                  >
                    🗑️
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div>Заметок не найдено.</div>
          )}
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(Notes);
