// RegistrationPage.js
import React, { useState } from 'react';

const RegistrationPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [name, setName] = useState('');
  const [nickname, setNickname] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [avatar, setAvatar] = useState('');

  const isPasswordValid = () => {
    // Длина пароля >= 8 символам;
    // Пароль содержит хотя бы одну заглавную букву;
    // Пароль содержит хотя бы одну строчную букву;
    // Пароль содержит хотя бы одну цифру.
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,}$/;
    return passwordRegex.test(password);
  };

  const handleRegistration = async () => {
    if (!isPasswordValid()) {
      console.error('Пароль не соответствует требованиям.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/registration', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password,
          confirmPassword,
          name,
          nickname,
          age,
          gender,
          avatar,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Ошибка регистрации:', errorData.error);
        return;
      }

      const newUser = await response.json();
      console.log('Зарегистрирован пользователь:', newUser);
    } catch (error) {
      console.error('Ошибка регистрации:', error.message);
    }
  };

  return (
    <div>
      <h2>Регистрация</h2>
      <form>
        {/* Ваши поля ввода */}
        <button type="button" onClick={handleRegistration}>
          Зарегистрироваться
        </button>
      </form>
    </div>
  );
};

export default RegistrationPage;
