import React, { useContext, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { connect } from 'react-redux';

function Home({ user }) {
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Моя Домашняя Страница";
  }, []);

  if (!user) {
    return null;
  }

  const registrationDate = new Date(user.date);

  const formattedDate = registrationDate.toLocaleString("ru-RU", {
    day: "2-digit",
    month: "2-digit",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
  });

  const handleGoToNotes = () => {
    navigate("/notes");
  };

  return (
    <div>
      <header>
        <div className="navigation-left">Привет, {user.name}</div>
        <div className="navigation-right">
          <Link to="/home" className="navigation-link about">
            Обо мне
          </Link>
          <Link to="/notes" className="navigation-link">
            Заметки
          </Link>
          <Link to="/login" className="navigation-link">
            Выйти
          </Link>
        </div>
      </header>
      <div className="content">
        <h1>Обо мне</h1>
        <p>
          <strong>Почта:</strong>{" "}
          <span style={{ color: "gray" }}>{user.email}</span>
        </p>
        <p>
          <strong>Имя:</strong>{" "}
          <span style={{ color: "gray" }}>{user.name}</span>
        </p>
        <p>
          <strong>Никнейм:</strong>{" "}
          <span style={{ color: "gray" }}>{user.nickname}</span>
        </p>
        <p>
          <strong>Возраст:</strong>{" "}
          <span style={{ color: "gray" }}>{user.age}</span>
        </p>
        <p>
          <strong>Пол:</strong>{" "}
          <span style={{ color: "gray" }}>{user.gender}</span>
        </p>
        <p>
          <strong>Дата регистрации:</strong>{" "}
          <span style={{ color: "gray" }}>{formattedDate}</span>
        </p>
        <button onClick={handleGoToNotes}>Перейти к заметкам</button>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(Home);
