// LoginPage.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Получение данных о пользователе из сервера
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Ошибка входа:', errorData.error);
        if (response.status === 401) {
          console.error('Неправильный email или пароль');
        }
        return;
      }

      const user = await response.json();
      onLogin(user);

      // После успешного входа перенаправляем на домашнюю страницу
      navigate('/home');
    } catch (error) {
      console.error('Ошибка входа:', error.message);
      res.status(500).json({ error: 'Внутренняя ошибка сервера', details: error.message });
    }


  };

  return (
    <div>
      <h1>Авторизация</h1>
      <form onSubmit={handleLogin}>
        <label>
          Электронная почта:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <br />
        <label>
          Пароль:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="submit">Войти</button>
      </form>

      {/* Добавляем кнопку для перехода на страницу регистрации */}
      <p>
        Еще нет аккаунта? <Link to="/registration">Зарегистрируйтесь</Link>
      </p>
    </div>
  );
};

export default Login;

const LoginPage = () => {
  const handleLogin = (userData) => {
    // Здесь вы можете добавить логику входа, например, перенаправление на другую страницу
    console.log('Logged in with:', userData);
  };

  return (
    <div>
      <Login onLogin={handleLogin} />
    </div>
  );
};

export { LoginPage };
