import { z } from "zod";

export const Пользователь = z.object({
  электроннаяПочта: z
    .string()
    .email({ message: "Пожалуйста, введите корректный адрес электронной почты." }),
  пароль: z
    .string()
    .min(8)
    .regex(/[A-Z]/, {
      message: "Пароль должен содержать как минимум одну заглавную букву.",
    })
    .regex(/[a-z]/, {
      message: "Пароль должен содержать как минимум одну строчную букву.",
    })
    .regex(/[0-9]/, { message: "Пароль должен содержать как минимум одну цифру." }),
  дата: z.number(),
});
