// RegistrationForm.js
import React from 'react';
import { useForm } from 'react-hook-form';
import { useHistory } from 'react-router-dom';

const RegistrationForm = () => {
  const { register, handleSubmit, errors, watch } = useForm();
  const history = useHistory();

  const onSubmit = (data) => {
    // В реальном приложении отправьте данные на сервер для регистрации пользователя
    console.log(data);
    // Переход на другую страницу после успешной регистрации
    history.push('/login');
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <label>Email:</label>
      <input name="email" ref={register({ required: true, pattern: /^\S+@\S+$/i })} />
      {errors.email && <span>This field is required and must be a valid email.</span>}

      <label>Password:</label>
      <input name="password" type="password" ref={register({ required: true, minLength: 6 })} />
      {errors.password && <span>This field is required and must be at least 6 characters.</span>}

      <label>Confirm Password:</label>
      <input
        name="confirmPassword"
        type="password"
        ref={register({ required: true, validate: (value) => value === watch('password') })}
      />
      {errors.confirmPassword && <span>Passwords do not match.</span>}

      <button type="submit">Register</button>
    </form>
  );
};

export default RegistrationForm;
