// RegistrationPage.js
import React, { useState } from 'react';

const RegistrationPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [name, setName] = useState('');
  const [nickname, setNickname] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState(''); // Заменено на строку для хранения буквы пола
  const [avatar, setAvatar] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [confirmPasswordError, setConfirmPasswordError] = useState('');
  const [genderError, setGenderError] = useState('');

  const isPasswordValid = () => {
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,}$/;
    return passwordRegex.test(password);
  };

  const isEmailValid = () => {
    const emailRegex = /\S+@\S+\.\S+/;
    return emailRegex.test(email);
  };

  const handleOnChange = () => {
    setEmailError('');
    setPasswordError('');
    setConfirmPasswordError('');
    setGenderError('');
  };

  const handleRegistration = async () => {
    if (!isEmailValid()) {
      setEmailError('Введите корректный email.');
      return;
    }

    if (!isPasswordValid()) {
      setPasswordError('Пароль не соответствует требованиям.');
      return;
    }

    if (password !== confirmPassword) {
      setConfirmPasswordError('Пароли не совпадают.');
      return;
    }

    if (!gender) {
      setGenderError('Выберите пол.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/registration', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password,
          confirmPassword,
          name,
          nickname,
          age,
          gender,
          avatar,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error(`Ошибка регистрации: ${errorData.error}`);
        return;
      }

      const newUser = await response.json();
      console.log('Зарегистрирован пользователь:', newUser);
    } catch (error) {
      console.error(`Ошибка регистрации: ${error.message}`);
    }
  };

  return (
    <div>
      <h2>Регистрация</h2>
      <form>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        <div>
          <label>Пароль:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        <div>
          <label>Повторите пароль:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => {
              setConfirmPassword(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        <div>
          <label>Имя:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => {
              setName(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        <div>
          <label>Никнейм:</label>
          <input
            type="text"
            value={nickname}
            onChange={(e) => {
              setNickname(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        <div>
          <label>Возраст:</label>
          <input
            type="number"
            value={age}
            onChange={(e) => {
              setAge(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        <div>
          <label>Пол:</label>
          <label>
            Мужской
            <input
              type="checkbox"
              name="gender"
              value="M"
              checked={gender === 'M'}
              onChange={(e) => {
                setGender(e.target.value);
                handleOnChange();
              }}
            />
          </label>
          <label>
            Женский
            <input
              type="checkbox"
              name="gender"
              value="F"
              checked={gender === 'F'}
              onChange={(e) => {
                setGender(e.target.value);
                handleOnChange();
              }}
            />
          </label>
        </div>
        <div>
          <label>Ссылка на аватар:</label>
          <input
            type="text"
            value={avatar}
            onChange={(e) => {
              setAvatar(e.target.value);
              handleOnChange();
            }}
          />
        </div>
        {emailError && <div style={{ color: 'red' }}>{emailError}</div>}
        {passwordError && <div style={{ color: 'red' }}>{passwordError}</div>}
        {confirmPasswordError && <div style={{ color: 'red' }}>{confirmPasswordError}</div>}
        {genderError && <div style={{ color: 'red' }}>{genderError}</div>}
        <button type="button" onClick={handleRegistration}>
          Зарегистрироваться
        </button>
      </form>
    </div>
  );
};

export default RegistrationPage;
