// UpdateNote.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { connect } from 'react-redux';
import { editNote, setUser } from '../redux/actions';

function UpdateNote({ user }) {
  const { noteId } = useParams();
  const [note, setNote] = useState(null);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const updateNote = () => {
    if (validateInputs()) {
      const updatedNote = {
        id: noteId,
        authorId: user.id,
        title,
        text: body,
        createdAt: note.createdAt,
      };

      fetch(`http://localhost:5001/notes/${noteId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedNote),
      })
        .then((response) => {
          if (response.ok) {
            dispatch(editNote(noteId, updatedNote));  // Dispatch the editNote action
            navigate(`/view-note/${noteId}`);
          } else {
            console.error("Failed to update note");
          }
        })
        .catch((error) => {
          console.error("Error updating note:", error);
        });
    }
  };

  const goBack = () => {
    navigate(`/notes`);
  };

  return (
    <div>
      <nav className="navigation">
        <div className="navigation-left">Привет, {user.email}</div>
        <div className="navigation-right">
          <Link to="/home" className="navigation-link">
            Обо мне
          </Link>
          <Link to="/notes" className="navigation-link">
            Заметки
          </Link>
          <Link to="/login" className="navigation-link">
            Выйти
          </Link>
        </div>
      </nav>
      <div className="updateNote-container">
        <h1>Изменить заметку</h1>
        <div>
          <input
            className="title-input"
            type="text"
            id="editNote-title"
            value={title}
            onChange={handleTitleChange}
          />
          <div className="error">{errors.title && <p>{errors.title}</p>}</div>
        </div>
        <div>
          <textarea
            id="editNote-body"
            className="note-text"
            value={body}
            onChange={handleBodyChange}
          />
        </div>
        <div className="button-container">
          <button className="update-button" onClick={updateNote}>
            Обновить
          </button>
          <button className="back-button" onClick={goBack}>
            Назад
          </button>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps)(UpdateNote);
