import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../components/AuthContext";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const { handleLogin } = useContext(AuthContext);

  function handleLoginSubmit() {
    fetch("http://localhost:5001/users")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        if (Array.isArray(data)) {
          const user = data.find(
            (user) => user.email === email && user.password === password
          );

          if (user) {
            handleLogin(user);
            navigate("/home");
          } else {
            setErrors({ email: "Пользователь не найден или данные введены неверно" });
          }
        } else {
          setErrors({ email: "Неправильный формат данных" });
        }
      })
      .catch((error) => {
        console.error("Ошибка входа:", error);
        setErrors({ email: "An error occurred during login" });
      });
  }

  return (
    <div className="login-container">
      <h1>Login</h1>
      <div className="form-group">
        <input
          className="input-field"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {errors.email && <div className="error">{errors.email}</div>}
      </div>
      <div className="form-group">
        <input
          className="input-field"
          placeholder="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {errors.password && <div className="error">{errors.password}</div>}
      </div>
      <button className="login-button" onClick={handleLoginSubmit}>
        Login
      </button>
    </div>
  );
}
