// Login.js
import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Получение данных о пользователе из db.json
      const response = await fetch('/path/to/db.json'); // Замените на реальный путь
      const data = await response.json();

      // Поиск пользователя по введенной электронной почте и паролю
      const user = data.users.find((user) => user.email === email && user.password === password);

      if (user) {
        // Если аутентификация успешна, вызываем функцию onLogin с данными пользователя
        onLogin(user);
      } else {
        // Обработка ошибки входа
        console.error('Неверные учетные данные');
      }
    } catch (error) {
      console.error('Ошибка чтения базы данных:', error.message);
    }
  };

  return (
    <div>
      <h1>Авторизация</h1>
      <form onSubmit={handleLogin}>
        <label>
          Электронная почта:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <br />
        <label>
          Пароль:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="submit">Войти</button>
      </form>
    </div>
  );
};

export default Login;
