import React, { useContext, useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../components/AuthContext";

export default function ОбновлениеЗаметки() {
  const { user } = useContext(AuthContext);
  const { noteId } = useParams();
  const [note, setNote] = useState(null);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const загрузитьДанныеЗаметки = async () => {
      try {
        const response = await fetch(`http://localhost:5001/notes/${noteId}`);
        if (response.ok) {
          const fetchedNote = await response.json();
          setNote(fetchedNote);
          setTitle(fetchedNote.title);
          setBody(fetchedNote.text);
        } else {
          console.error("Не удалось получить заметку");
        }
      } catch (error) {
        console.error("Ошибка при получении заметки:", error);
      }
    };

    загрузитьДанныеЗаметки();
  }, [noteId]);

  const validateInputs = () => {
    const newErrors = {};

    if (title.trim() === "") {
      newErrors.title = "Название обязательно";
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleBodyChange = (event) => {
    setBody(event.target.value);
  };

  const updateNote = () => {
    if (validateInputs()) {
      const updatedNote = {
        id: noteId,
        authorId: user.id,
        title,
        text: body,
        createdAt: note.createdAt,
      };

      fetch(`http://localhost:5001/notes/${noteId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedNote),
      })
        .then((response) => {
          if (response.ok) {
            navigate(`/view-note/${noteId}`);
          } else {
            console.error("Не удалось обновить заметку");
          }
        })
        .catch((error) => {
          console.error("Ошибка при обновлении заметки:", error);
        });
    }
  };

  const goBack = () => {
    navigate(`/notes`);
  };

  return (
    <div>
      <nav className="navigation">
        <div className="navigation-left">Привет, {user.email}</div>
        <div className="navigation-right">
          <Link to="/about" className="navigation-link">
            О нас
          </Link>
          <Link to="/notes" className="navigation-link">
            Заметки
          </Link>
          <Link to="/logout" className="navigation-link">
            Выйти
          </Link>
        </div>
      </nav>
      <div className="updateNote-container">
        <h1>Редактировать заметку</h1>
        <div>
          <input
            className="title-input"
            type="text"
            id="editNote-title"
            value={title}
            onChange={handleTitleChange}
          />
          <div className="error">{errors.title && <p>{errors.title}</p>}</div>
        </div>
        <div>
          <textarea
            id="editNote-body"
            className="note-text"
            value={body}
            onChange={handleBodyChange}
          />
        </div>
        <div className="button-container">
          <button className="update-button" onClick={updateNote}>
            Обновить
          </button>
          <button className="back-button" onClick={goBack}>
            Назад
          </button>
        </div>
      </div>
    </div>
  );
}
