// HomePage.js
import React, { useState, useEffect } from 'react';

const HomePage = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/user'); // Предположим, что у вас есть маршрут /user, который возвращает данные о пользователе
        if (!response.ok) {
          throw new Error('Ошибка получения данных о пользователе');
        }
        const user = await response.json();
        setUserData(user);
      } catch (error) {
        console.error('Произошла ошибка:', error.message);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div>
      {userData ? (
        <div>
          <div>
            <span>Hello, {userData.nickname}</span>
          </div>
          <div>
            <h2>About me</h2>
            <div>
              <strong>Имя:</strong> {userData.name}
            </div>
            <div>
              <strong>Email:</strong> {userData.email}
            </div>
            <div>
              <strong>Возраст:</strong> {userData.age}
            </div>
            <div>
              <strong>Пол:</strong> {userData.gender}
            </div>
            <div>
              <strong>Ссылка на аватар:</strong> {userData.avatar}
            </div>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default HomePage;
