import React, { useContext, useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../components/AuthContext";
import Header from "./Header";

export default function NoteCreator() {
  const { user } = useContext(AuthContext);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Create a New Note";
  }, []);

  const handleChangeTitle = (event) => {
    setTitle(event.target.value);
  };

  const handleChangeBody = (event) => {
    setBody(event.target.value);
  };

  const validateInputs = () => {
    const newErrors = {};

    if (title.trim() === "") {
      newErrors.title = "Title is required";
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const createNote = () => {
    if (validateInputs()) {
      const newNote = {
        authorId: user.id,
        title,
        text: body,
        createdAt: new Date().toISOString(),
      };

      fetch("http://localhost:5001/notes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newNote),
      })
        .then((response) => {
          if (response.ok) {
            navigate("/notes");
          } else {
            console.error("Failed to create note");
          }
        })
        .catch((error) => {
          console.error("Error creating note:", error);
        });
    }
  };

  const goBack = () => {
    navigate("/notes");
  };

  return (
    <div>
      <Header />
      <div className="note-container">
        <h1 style={{ textAlign: "center" }}>Create a New Note</h1>
        <input
          type="text"
          className="title-input"
          placeholder="Title"
          value={title}
          onChange={handleChangeTitle}
        />
        <div className="error">{errors.title && <p>{errors.title}</p>}</div>
        <textarea
          className="note-text"
          placeholder="Note text..."
          value={body}
          onChange={handleChangeBody}
        />
        <div className="button-group">
          <button className="create-button" onClick={createNote}>
            Create
          </button>
          <button className="back-button" onClick={goBack}>
            Go Back
          </button>
        </div>
      </div>
    </div>
  );
}
