import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import PrivateRoute from './components/PrivateRoute';

const App = () => {
  const [isAuthenticated, setAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Проверка аутентификации пользователя при загрузке приложения
    checkAuthentication();
  }, []);

  const checkAuthentication = async () => {
    try {
      const response = await fetch('/api/check-auth'); // Замените '/api/check-auth' на ваш реальный эндпоинт
      const data = await response.json();

      if (data.success) {
        setAuthenticated(true);
        setUser(data.user);
      } else {
        setAuthenticated(false);
        setUser(null);
      }
    } catch (error) {
      console.error('Error checking authentication:', error.message);
    }
  };

  const handleLogin = async (userData) => {
    try {
      const response = await fetch('/api/login', { // Замените '/api/login' на ваш реальный эндпоинт
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      const data = await response.json();

      if (data.success) {
        setAuthenticated(true);
        setUser(data.user);
      } else {
        console.error('Login failed:', data.message);
      }
    } catch (error) {
      console.error('Error during login:', error.message);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('/api/logout'); // Замените '/api/logout' на ваш реальный эндпоинт
      const data = await response.json();

      if (data.success) {
        setAuthenticated(false);
        setUser(null);
      } else {
        console.error('Logout failed:', data.message);
      }
    } catch (error) {
      console.error('Error during logout:', error.message);
    }
  };

  return (
    <Router>
      <div>
        <Routes>
          {/* Начальная страница - страница входа */}
          <Route path="" element={<LoginPage onLogin={handleLogin} />} />

          {/* Защищенный маршрут для домашней страницы */}
          <PrivateRoute
            path="/"
            element={<HomePage user={user} onLogout={handleLogout} />}
            isAuthenticated={isAuthenticated}
            fallbackPath="/login"
          />

          {/* Добавим перенаправление на страницу входа, если маршрут не существует */}
          <Route path="/*" element={<Navigate to="/login" replace />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
